import React, { Component } from 'react';
import s from './Header.module.css';

class Header extends Component {
    constructor(props) {
        super(props) 
        this.state = {
            lang: ''
        }
    }


    changeLang = (e) => {
        const {innerHTML} = e.target;
        // const {value} = e.target;
        // const curLang = null;
        // if(lang === 'eng') {
        //     curLang =  'rus';
        // } else {
        //     curLang = "eng";
        // }
        console.log("111");
        this.setState({
           lang: innerHTML
        })
        console.log(innerHTML);
    }

    submitLang = () => {
        console.log("222");
        this.props.getLang(this.state.langs)
    }

    render() {

        const menuList = this.props.menuItems.map(item => {
            return <li key={item.id}>{item.content}</li>
        })
        return (

            <header id={s.header}>
                    <nav>
                        <div id={s.toggleWrap}>
                        <label htmlFor={s.toggle}>&#9776;</label>
                        <div id={s.lang}>
                            <span value="eng" onClick={this.changeLang}>eng</span> 
                            <span value="rus" onClick={this.changeLang}>рус</span>
                        </div>
                        </div>
                        <input type="checkbox" id={s.toggle}/>

                        <div className={s.menu}>
                            <ul>
                                {menuList}
                            </ul>
                        </div>
                    </nav>
                <div id={s.headerMain}>
                    <h1>{this.props.header}</h1>
                    <div id={s.dashline}></div>
                    <p>{this.props.p}</p>
                    <div id={s.span}></div>
                </div>


            </header>

        )
    }
}

export default Header
