import React, { Component } from 'react';
import FilterPanel from './FilterPanel.jsx';
import Photo from './Photo.jsx';
import s from './Projects.module.css';

import branding1 from "../img/projects/projects_branding1.jpg";
import branding2 from "../img/projects/projects_branding2.jpg";
import branding3 from "../img/projects/projects_branding3.jpg";
import illustrations1 from "../img/projects/projects_illustrations1.jpg";
import webDesign1 from "../img/projects/projects_webDesign1.jpg";
import mobileUI1 from "../img/projects/projects_mobileUI1.jpg";




class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = {
            displayedCategories: PHOTODATA,
            active: false
        }
    }

    render() {
        let PHOTODATA = [{
			id: 1,
			category: 'fruits',
			imageUrl: branding1
		},{
			id: 2,
			category: 'fruits',
			imageUrl: branding2
		},
		{
			id: 3,
			category: 'fruits',
			imageUrl: branding3
		},{
			id: 4,
			category: 'vegetables',
			imageUrl: illustrations1
		},{
			id: 5,
			category: 'vegetables',
			imageUrl: webDesign1
		},{
			id: 6,
			category: 'another',
			imageUrl: mobileUI1
		}];


        // const menu = this.props.menu.map(menuItem => {
        //     return <li key={menuItem.id}>{menuItem}</li>
        // }) ////////////////////////////////////////////////////////////////




        // getInitialState = () => {
        //     return {
        //         displayedCategories: PHOTODATA,
        //         active: false
        //     };
        // }
 toggleActive = () => {
    this.setState({
        active: !this.state.active
    });
}

selectCategory = (element) => {
    console.log('element is: '+ element);
      let categoryName = element.toLowerCase();
            let displayedCategories = PHOTODATA.filter(function(el) {
                
                let searchValue = el.category.toLowerCase();
                return searchValue.indexOf(categoryName) !== -1;
            });

            this.setState({
                displayedCategories: displayedCategories

            });

}
resetFilter = (element) => {
    this.setState({
                displayedCategories: PHOTODATA
            });
}


/////////////////////////////render//////////
var buttonClass = this.state.active ? 'active' : '';
		var categoryToSelect = this.selectCategory;
		var getUniqueCategories=[];
		PHOTODATA.forEach(function(el){
			if(getUniqueCategories.indexOf(el.category) === -1 ) getUniqueCategories.push(el.category);
		})


        
        return (
            <div className="overlay-photogallery">
				<div className="filter-panel">
					{
						getUniqueCategories.map(function(el,i){
							var boundClick = categoryToSelect.bind(null,el);
							return <FilterPanel onClick={boundClick} category={el} key={i} />
						})

					}		
					<a className="resetBtn" onClick={this.resetFilter}> Reset Filter </a>
				</div>
				<div className="PhotoGallery">
					{

						this.state.displayedCategories.map(function(el){
									return <Photo key={el.id} imageUrl={el.imageUrl} category={el.category} />
								})

					}
				</div>
			</div>
            // <div id={s.projects}>
            //     <div className={s.wrapper}>
            //         <h1>{this.props.header}</h1>
            //         <div id={s.dashline}></div>
            //         <nav id={s.menu}>
            //             <ul>
            //                 {menu}
            //             </ul>
            //         </nav>
            //         <div className={s.imgWrapper}>
            //             <img src={this.state.branding1} alt="" />
            //             <img src={require("../img/projects/projects_branding2.jpg")} alt="" />
            //             <img src={require("../img/projects/projects_branding3.jpg")} alt="" />
            //             <img src={require("../img/projects/projects_illustrations1.jpg")} alt="" />
            //             <img src={require("../img/projects/projects_webDesign1.jpg")} alt="" />
            //             <img src={require("../img/projects/projects_mobileUI1.jpg")} alt="" />
            //         </div>
            //         <div id={s.btn}>Show me more</div>
            //     </div>
            // </div>
        )

    }
}

export default Projects
